import React, { useState } from "react";
import styled from "styled-components";
import { RowView, RowView2 } from "./commom_style";

const ModalBox = styled(RowView)`
  position: absolute;
  bottom: 0;
  width: 100%;
  max-width: 20rem;
  padding: 0.5rem 1rem;
  margin-bottom: 1rem;
  background-color: #10101dd6;
  border-radius: 8px;
  transition: all 0.4s;
  &.none {
    bottom: -20%;
    opacity: 0;
  }
`;
const Btn = styled.div`
  padding: 0.2rem 0.5rem;
  margin-left: 0.5rem;
  background-color: tomato;
  border-radius: 5px;
  &.cancel {
    color: darkgray;
    background-color: #4b4f63;
  }
`;

const Del_Modal = (props) => {
  const selectNum = props.selectNum;
  const setSelectNum = props.setSelectNum;

  const cancel = () => {
    setSelectNum("");
  };

  return (
    <ModalBox className="none">
      <div>정말 삭제하시겠습니까?</div>

      <RowView2>
        <Btn>삭제</Btn>
        <Btn className="cancel" onClick={cancel}>
          취소
        </Btn>
      </RowView2>
    </ModalBox>
  );
};

export default Del_Modal;
